---//怪物捕捉难度对于捕捉成功率的影响----------------------------------------------------------------
function ZCatchChanceFromDifficulty(monster_catchdifficulty)
	if type(monster_catchdifficulty)~="number" or monster_catchdifficulty<=0 or monster_catchdifficulty>8 then 
		monster_catchdifficulty=9
	end 
	local difficultyratio={0.8,0.7,0.6,0.5,0.4,0.3,0.2,0.1,0}
	return difficultyratio[monster_catchdifficulty]
end 

---//怪物气血对于捕捉成功率的影响--------------------------------------------------------------------
function ZCatchChanceFromHP(monster_hp)
	if type(monster_hp)~="number" or monster_hp<=0 or monster_hp>1 then 
		monster_hp=1
	end 
	local hpratio=math.min(math.max(1-0.5*monster_hp*monster_hp,0),1)
	return hpratio
end 

---//人物怪物级别差对于捕捉成功率的影响-----------------------------------------------------------
function ZCatchChanceFromLevels(monster_level,player_level)
	if type(monster_level)~="number" or monster_level<1 or monster_level>150 then 
		monster_level=150
	end 
	if type(player_level)~="number" or player_level<1 or player_level>150 then 
		player_level=1
	end 
	local levelratio=1
	if player_level>monster_level*2 then 
		levelratio=2
	elseif player_level<monster_level*0.5 then 
		levelratio=0.5
	else
		levelratio=1
	end 	
	return levelratio
end 

---//宠物捕捉主函数---------------------------------------------------------------
function PetBedgeCatch(monster_id, monster_level, monster_raceinfo, monster_catchdifficulty,monster_hp,player_level)
	local pet_id,msg_channel,msg_id=0,0,301
	local catchskilllvl=0							---//race对应的捕捉技能等级
	local withcatchskillid=0								---//race对应的捕捉技能id
	local catchfinnalchance=0					---//捕捉的总成功率
	local iscatch=0										---//是否捕捉成功
	local skill_alvl,skill_blvl=0,0		---//捕捉技能的加点等级、总等级
	local maxraceinfo=65536						---//最大种族允许的编号(2^16)
	local caughtpets=ZStaticMonsterList()
	
	if type(monster_id)~="number" or type(monster_raceinfo)~="number" then
	 ---//print("i am here",monter_id,monster_raceinfo)
	 return pet_id,msg_channel,302
	end 
	if type(monster_level)~="number" or monster_level<1 or monster_level>150 then
	 ---//print("i am here",monster_level)
	 return pet_id,msg_channel,303
	end 
	if type(monster_raceinfo)~="number" or monster_raceinfo<=0 or monster_raceinfo>maxraceinfo or ZCatchIsAvailableMonsRace(monster_raceinfo)==0 then
	 ---//print("i am here",monster_raceinfo)
	 return pet_id,msg_channel,304
	end 	
	if type(monster_catchdifficulty)~="number" or monster_catchdifficulty<=0 or monster_catchdifficulty>8 then
	 ---//print("i am here",monster_catchdifficulty)
	 return pet_id,msg_channel,305
	end 	
	if type(monster_hp)~="number" or monster_hp<=0 or monster_hp>1 then
	---//print("i am here",monster_hp)
	 return pet_id,msg_channel,306
	end 	
	if type(player_level)~="number" or player_level<=0 or player_level>150 then
	 ---//print("i am here",player_level)
	 return pet_id,msg_channel,307
	end 	

	
	withcatchskillid,msg_id=ZSkillsOfPetCatch(monster_raceinfo)
	skill_alvl,skill_blvl=PlayerQuerySkilllvl(withcatchskillid)
	
	catchfinnalchance=10*skill_alvl*ZCatchChanceFromDifficulty(monster_catchdifficulty)*ZCatchChanceFromHP(monster_hp)*ZCatchChanceFromLevels(monster_level,player_level)
	iscatch=ZMiscThrowDice(catchfinnalchance)
	if iscatch==1 then 
		for i,v in pairs(caughtpets) do
			if v.monster_id==monster_id then 
				pet_id=v.pet_id
			end 
		end 
			msg_id=310+msg_id	
	else
		pet_id=0
		msg_id=309
	end 
		return pet_id,msg_channel,msg_id
	
end
